const expect = chai.expect;

describe("User Functions", () => {
  let user_id: string = "";
  let token: string = "";

  it("*******registers/adds a user", async () => {
    const user: UserObject = {
      id: "test",
      phoneNumber: 5444,
      whatsappNumber: 5444,
